import React from "react";
import 'bootstrap/dist/css/bootstrap.min.css';
import Button from 'react-bootstrap/Button';
import ItemForm from "./ItemForm";
import './resources/Form.css'

const Temperature = ({question, setForm, formData, navigation }) => {
  const { temperature } = formData;

  const { next } = navigation;

  return (
    <div className="form">
      <h3>Hur hög är din kroppstemperatur?</h3>
      <ItemForm
        label="Under 38,0 grader"
        name={temperature}
        value="under38"
        onChange={setForm}
      />
      <ItemForm
        label="38,0 - 39,4 grader"
        name={temperature}
        value="over38"
        onChange={setForm}
      />
      <ItemForm
        label="39,5 grader eller över"
        name={temperature}
        value={"over39"}
        onChange={setForm}
      />
      <div>
        <Button onClick={next}>Next</Button>
      </div>
    </div>
  );
};



export default Temperature;
