import React, { Component } from 'react';
import { useForm, useStep } from "react-hooks-helper";

import Temperature from "./Temperature";
import Cough from "./Cough";
import Breathing from "./Breathing";
import Energy from "./Energy";
import Review from "./Review";
import Success from "./Success";
import Contact from "./Contact"

import "./resources/Form.css";

class MainForm extends Component {
  state = {
      step: 1,
      firstName: '',
      lastName: '',
      email: '',
      age: '',
      city: '',
      country: ''
  }

  nextStep = () => {
      const { step } = this.state
      this.setState({
          step : step + 1
      })
  }

  prevStep = () => {
      const { step } = this.state
      this.setState({
          step : step - 1
      })
  }

  handleChange = input => event => {
      this.setState({ [input] : event.target.value })
  }

  render(){
      const {step} = this.state;
      const { temperature, cough, breathing, energy, contact, atRirk, immunocmpromised, ageGroup } = this.state;
      const values = {  temperature, cough, breathing, energy, contact, atRirk, immunocmpromised, ageGroup  };
      switch(step) {
      case 1:
          return <Temperature
                  nextStep={this.nextStep}
                  handleChange = {this.handleChange}
                  values={values}
                  />
      case 2:
          return <Cough
                  nextStep={this.nextStep}
                  prevStep={this.prevStep}
                  handleChange = {this.handleChange}
                  values={values}
                  />
      case 3:
          return <Breathing
                  nextStep={this.nextStep}
                  prevStep={this.prevStep}
                  values={values}
                  />
      case 4:
        return <Energy
                  nextStep={this.nextStep}
                  prevStep={this.prevStep}
                  values={values}
                  />
      case 5:
        return <Contact
                  nextStep={this.nextStep}
                  prevStep={this.prevStep}
                  values={values}
                  />
      case 6:
        return <AtRisk
                  nextStep={this.nextStep}
                  prevStep={this.prevStep}
                  values={values}
                  />
      case 7:
        return <Immunocmpromised
                  nextStep={this.nextStep}
                  prevStep={this.prevStep}
                  values={values}
                  />
      case 7:
        return <AgeGroup
                  nextStep={this.nextStep}
                  prevStep={this.prevStep}
                  values={values}
                  />
      case 4:
          return <Success />
      }
  }
}
export default MainForm;
