import React, { Component } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import './resources/Form.css'
import * as firebase from "firebase/app";


var config = {
  apiKey: "apiKey",
  authDomain: "howareyoufeelingtoday.firebaseapp.com",
  databaseURL: "https://howareyoufeelingtoday-51083.firebaseio.com",
  storageBucket: "us-central1.appspot.com"
};
firebase.initializeApp(config);

// Get a reference to the database service
var database = firebase.database();


class Success extends Component{
    render(){
        const {values: { temperature, cough, breathing, energy, contact, atRisk, immune, ageGroup }} = this.props;
        return(
          
            <div>
                <h1 className="ui centered">Thank you for contributing!</h1>
            </div>
        )
    }
}

function writeData(temperature, cough, breathing, energy, contact, atRisk, immune, ageGroup) {
    firebase.database().ref('responses/').set({
      temperature: temperature, 
      cough: cough, 
      breathing: breathing, 
      energy: energy, 
      contact: contact, 
      atRisk: atRisk, 
      immune: immune, 
      ageGroup: ageGroup
    });
  }

export default Success;
