import React, {Component} from "react";
import 'bootstrap/dist/css/bootstrap.min.css';
import { Form, List } from 'semantic-ui-react';
import Button from 'react-bootstrap/Button';
import './resources/Form.css'

class Confirmation extends Component{
    saveAndContinue = (e) => {
        e.preventDefault();
        this.props.nextStep();
    }

    back  = (e) => {
        e.preventDefault();
        this.props.prevStep();
    }

    render(){
        const {values: { temperature, cough, breathing, energy, contact, atRirk, immune, ageGroup }} = this.props;

        return(
            <div>
                <h1 className="ui centered">Confirm your Details</h1>
                <p>Click Confirm if the following details have been correctly entered</p>
                <List>
                    <List.Item>
                        <List.Content>temperature: {temperature}</List.Content>
                    </List.Item>
                    <List.Item>
                        <List.Content>cough: {cough}</List.Content>
                    </List.Item>
                    <List.Item>
                        <List.Content>Breathing: {breathing}
                        </List.Content>
                    </List.Item>
                    <List.Item>
                        <List.Content>{energy} Years</List.Content>
                    </List.Item>
                    <List.Item>
                        <List.Content>contact: {contact}</List.Content>
                    </List.Item>
                    <List.Item>
                        <List.Content>atRirk: {atRirk}</List.Content>
                    </List.Item>
                    <List.Item>
                        <List.Content>immune: {immune}</List.Content>
                    </List.Item>
                    <List.Item>
                        <List.Content>ageGroup: {ageGroup}</List.Content>
                    </List.Item>
                </List>

                <Button onClick={this.back}>Back</Button>
                <Button onClick={this.saveAndContinue}>Confirm</Button>
            </div>
        )
    }
}

export default Confirmation;
